mapper.update:
UPDATE USERS
SET pw = #{pw}
WHERE id = #{id}

mapper.select:
SELECT *
FROM users
WHERE id = #{id}
	AND pw = #{pw}

SELECT id
FROM users
WHERE name = #{name}
	AND email = #{email}

SELECT count(*)
FROM users
WHERE id = #{id}
	AND name = #{name}
	AND email = #{email}

SELECT count(*)
FROM users
WHERE id = #{id}
