mapper.select:
SELECT IFNULL(MIN(r.position),
		(SELECT r1.position FROM reply r1 WHERE rp_num = #{parentNum})) 
			FROM reply r WHERE parentNum = #{parentNum}

SELECT * 
FROM (
	SELECT * 
	FROM reply
	WHERE fb_num = #{fb_num}
	ORDER BY position
	LIMIT #{offset}, #{limit}
) A ORDER BY position DESC

SELECT * 
FROM (
	SELECT * 
	FROM reply
	WHERE mb_num = #{mb_num}
	ORDER BY position
	LIMIT #{offset}, #{limit}
) A ORDER BY position DESC

SELECT count(*) 
FROM reply 
WHERE mb_num = #{mb_num}

SELECT count(*) 
FROM reply 
WHERE tb_num = #{tb_num}

SELECT * 
FROM (
	SELECT * 
	FROM reply
	WHERE tb_num = #{tb_num}
	ORDER BY position
	LIMIT #{offset}, #{limit}
) A ORDER BY position DESC
